/** @format */

import React, { useEffect, useState } from "react";
import "./CreateModal.css";
import axios from "axios";
const CreateModal = ({setModal, type, modal}) => {
  const [postData, setPostData] = useState({
    // id: 4,
    userName: "",
    department: "",
    birthDate: "",
    dateOfEmployment: "",
    wage: 0,
  });
console.log(modal)
  const handleCreatePost = () => {
    // Format date strings before making the POST request
    const formattedPostData = {
      ...postData,
      birthDate: formatDateString(postData.birthDate),
      dateOfEmployment: formatDateString(postData.dateOfEmployment),
    };

    fetch("https://api.example.com/posts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formattedPostData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success:", data);
        // Handle success, e.g., update state or show a success message
      })
      .catch((error) => {
        console.error("Error:", error);
        // Handle error, e.g., show an error message
      });
  };

  const formatDateString = (dateString) => {
    const dateObject = new Date(dateString);
    return dateObject.toISOString(); // Format the date as an ISO string
  };
  const fetchDataById = async (id) => {
    try {
      const response = await axios.get(`https://localhost:7007/api/Employees/${id}`);
      setPostData(response.data);
    } catch (error) {
      // setError(error);
    } finally {
      // setIsLoading(false);
    }
  };

  useEffect(() => {
    if(modal==modal){
      fetchDataById(modal);
    }
  }, []);
  return (
    <>
      <div className="modal">
        <form>
          <div className="input-box">
            <div className="input">
              <label>Name</label>
              <input
                type="text"
                onChange={(e) =>
                  setPostData((prev) => ({ ...prev, userName: e.target.value }))
                }
              />
            </div>
            <div className="input">
              <label>Department</label>
              <input
                type="text"
                onChange={(e) =>
                  setPostData((prev) => ({
                    ...prev,
                    department: e.target.value,
                  }))
                }
              />
            </div>
          </div>
          <div className="input-box">
            <div className="input">
              <label>Age</label>
              <input type="date"  />
            </div>
            <div className="input">
              <label>Salary</label>
              <input
                type="text"
                onChange={(e) =>
                  setPostData((prev) => ({ ...prev, wage: e.target.value }))
                }
              />
            </div>
          </div>
          <div className="input-box">
            <div className="input">
              <label>Date of Employment</label>
              <input type="date" />
            </div>
          </div>
          <div className="btn-box">
            <button onClick={()=>setModal(false)}>Cancel</button>
            <button type="submit" onClick={handleCreatePost}>
              add
            </button>
          </div>
        </form>
      </div>
      <div className="w-screen"></div>
    </>
  );
};

export default CreateModal;
